import { useEffect, useState } from "react";
import { db } from './firebase-config'
import { collection, getDocs, addDoc, doc, updateDoc, deleteDoc } from "@firebase/firestore";

function App() {
    const [users, setUsers] = useState([])
    const [name, setName] = useState("")
    const [age, setAge] = useState(0)
    const UsersCollectionRef = collection(db, "backendData")

    useEffect(() => {
    const getUsersData = async () => {
      const data = await getDocs(UsersCollectionRef)
      setUsers(data.docs.map((elem) => ({ ...elem.data(), id: elem.id })))
    }

    getUsersData()
  }, [])

    const CreateUser = async () => {
        await addDoc(UsersCollectionRef, { Name: name, age: age })
        window.location.reload()
    }

    const increaseAge = async (id, age) => {
    const userDoc = doc(db, "users", id)
    const NewAge = { age: Number(age) + 1 }
    await updateDoc(userDoc, NewAge)
    window.location.reload()
  }

  const deleteUser = async (id) => {
    const userDoc = doc(db, "users", id)
    await deleteDoc(userDoc)
    window.location.reload()
  }

  return (
    <>
    <p><img src="https://www.umkc.edu/news/posts/2022/april/april-pics/refreshed-umkc-logo.png" alt="UMKC Logo" width="161" height="97" /></p>
    <h2>Please select a class below to edit, update or close.</h2>
    <table class="editorDemoTable" style="height: 80px; width: 582px;">
        <tbody>
        <tr>
        <td style="width: 106.75px;"><strong>Job</strong></td>
        <td style="width: 157.25px;"><strong>Type</strong></td>
        <td style="width: 200px;"><strong>Number of Positions</strong></td>
        <td style="width: 83px;"><strong>Status</strong></td>
        <td style="width: 83px;"><strong>Delete</strong></td>
        </tr>
        <tr>
        <td style="width: 106.75px;">CS441</td>
        <td style="width: 157.25px;">GTA</td>
        <td style="width: 200px;">0</td>
        <td style="width: 83px;">Open</td>
        <td style="width: 83px;">&nbsp;</td>
        </tr>
        <tr>
        <td style="width: 106.75px;">CS320</td>
        <td style="width: 157.25px;">Grader</td>
        <td style="width: 200px;">1</td>
        <td style="width: 83px;">Closed</td>
        <td style="width: 83px;">&nbsp;</td>
        </tr>
        <tr>
        <td style="width: 106.75px;">CS490WD</td>
        <td style="width: 157.25px;">GTA</td>
        <td style="width: 200px;">2</td>
        <td style="width: 83px;">Open</td>
        <td style="width: 83px;">&nbsp;</td>
        </tr>
        </tbody>
    </table>
<p>&nbsp;</p>
</>
  );
}

export default App;
