import { React, useEffect, useState } from 'react';
import Avatar from "@material-ui/core/Avatar";
import Button from "@material-ui/core/Button";
import CssBaseline from "@material-ui/core/CssBaseline";
import TextField from "@material-ui/core/TextField";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Checkbox from "@material-ui/core/Checkbox";
import Link from "@material-ui/core/Link";
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import Container from "@material-ui/core/Container";
import { db } from "./firebase-config";
import { collection, getDocs, addDoc, doc, updateDoc, deleteDoc, query, where } from "firebase/firestore";

function Message() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {"UMKC CS 451-R Project"}
    </Typography>
  );
}

const useStyles = makeStyles(theme => ({
  "@global": {
    body: {
      backgroundColor: theme.palette.common.white
    }
  },
  paper: {
    marginTop: theme.spacing(8),
    display: "flex",
    flexDirection: "column",
    alignItems: "center"
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: "#296BBD"
  },
  form: {
    width: "100%",
    marginTop: theme.spacing(3)
  },
  submit: {
    margin: theme.spacing(3, 0, 2)
  }
}));

function SignUp() {
  // Define state variables to manage form input and job data
  const [jobs, setJobs] = useState([]);
  const [courseID, setCourseID] = useState("");
  const [term, setTerm] = useState("");
  const [type, setType] = useState("");
  const [numPositions, setNumPosition] = useState("");
  const [state, setState] = useState("");
  

  //popup card 
  const [users, setUsers] = useState([]);
  const [showPopup, setShowPopup] = useState(false);

  // reference to the "backenddata" collection in Firestore
  const JobCollectionRef = collection(db, "backenddata");
  // reference to the "profile" collection in Firestore
  const UserCollectionRef = collection(db, "profile");

  useEffect(() => {
    getUserData();
  }, []);

  // Fetch data from Firestore and update the state variable "jobs"
  const getUserData = async () => {
    const data = await getDocs(UserCollectionRef);
    setUsers(data.docs.map((elem) => ({ ...elem.data(), id: elem.id })));
  };

  // Use the useEffect hook to fetch data from Firestore when the component mounts
  useEffect(() => {
    getJobData();
  }, []);

  // Fetch data from Firestore and update the state variable "jobs"
  const getJobData = async () => {
    const data = await getDocs(JobCollectionRef);
    setJobs(data.docs.map((elem) => ({ ...elem.data(), id: elem.id })));
  };

  // Add a new job to Firestore
  const createJob = async () => {
    try {
      // Query Firebase for documents that match the specified courseID
      const jobQuerySnapshot = await getDocs(
        query(JobCollectionRef, where("Courseid", "==", courseID.toUpperCase()))
      );
      if (jobQuerySnapshot.docs.length === 0) {
        await addDoc(JobCollectionRef, {
          Courseid: courseID.toUpperCase(),
          Term: term,
          Type: type,
          NumPositions: numPositions,
          State: state,
        });
        window.location.reload();
      } else {
        alert(`A job with CourseID ${courseID.toUpperCase()} already exists.`);
      }
    } catch (error) {
      console.error("Error creating document: ", error);
    }
  };

  // Check user
  // Define an asynchronous function called "checkUser"
  const checkUser = async () => {

    await addDoc(JobCollectionRef, {
      Courseid: courseID,
      Term: term,
      Type: type,
      NumPositions: numPositions,
      State: state,
    });

    // Reload the page after the new document has been added
    window.location.reload();
  };
  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
        <Avatar className={classes.avatar}></Avatar>
        <Typography component="h1" variant="h5">
          Sign up
        </Typography>
        <form className={classes.form} onSubmit={handleSubmit} noValidate>
          <Grid container spacing={2}>
            <Grid item xs={12} sm={6}>
              <TextField
                autoComplete="fname"
                name="firstName"
                variant="outlined"
                required
                fullWidth
                id="firstName"
                label="First Name"
                autoFocus
                value={firstName}
                onChange={handleFirstNameChange}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                autoComplete="lname"
                name="lastName"
                variant="outlined"
                required
                fullWidth
                id="lastName"
                label="Last Name"
                value={lastName}
                onChange={handleLastNameChange}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                autoComplete="email"
                name="email"
                variant="outlined"
                required
                fullWidth
                id="email"
                label="Email Address"
                value={email}
                onChange={handleEmailChange}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                autoComplete="current-password"
                name="password"
                variant="outlined"
                required
                fullWidth
                type="password"
                id="password"
                label="Password"
                value={password}
                onChange={handlePasswordChange}
              />
            </Grid>
            {/* <Grid item xs={12}>
              <FormControlLabel
                control={<Checkbox value="allowExtraEmails" color="primary" />}
                label="I want to receive inspiration, marketing promotions and updates via email."
              />
            </Grid> */}
          </Grid>
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            className={classes.submit}
          >
            Sign Up
          </Button>
          <Grid container justifyContent="flex-end">
            <Grid item>
              <Link href="/signin" variant="body2">
                Already have an account? Sign in
              </Link>
            </Grid>
          </Grid>
        </form>
      </div>
      <Box mt={5}>
        <Message />
      </Box>
    </Container>
  );
  
}
export default SignUp;
